import { useState } from 'react';

export default function Exercise2() {
    const [name, setName] = useState("");
    const [fruit, setFruit] = useState("");
    let fruits = ["banana", "melon", "grapes", "carrot"]
    function checkName(e) {
        setName(e.target.value)
        if (fruit) {
            setTimeout(() => console.log(`${name} choose ${fruit}`), 100)   
        }
    }
    function checkFruit(e) {
        setFruit(e.target.value)
        if (name) {
            setTimeout(() => console.log(`${name} choose ${fruit}`), 100)  
        }
    }
    return (
        <div>
            <input id="name-input" onChange={(e) => checkName(e)} value={name} />
            <select id="select-input" onChange={(e) => checkFruit(e)} value={fruit}>
                {fruits.map((el) => <option value={el} key={el}>{el}</option>)}
            </select>
        </div>
    );
}
